import React from "react"
import { useState } from "react"
import { useDispatch } from "react-redux"
import { createPoke } from "../actions"

const CreatePoke = () => {

    let dispatch = useDispatch();

    let [form , setForm] = useState({
        name : "",
        id : "",
        type : ""
    })

    function HandleChange (e) {
        setForm({
            ...form, [e.target.name] : e.target.value
        })
        console.log(form);
    }

    function handleClick (e) {
        e.prevenDefault();
        dispatch(createPoke())

    }

    return (
        <div>
            <form action="">
                <label >PokeName</label>
                <input name="name" onChange={HandleChange} type="text" />
                <br />
                <label >PokeId</label>
                <input name="id" onChange={HandleChange} type="number" />
                <br />
                <label >Type</label>
                <input name="type" onChange={HandleChange} type="text" />
                <button type="submit" onClick={handleClick}>Crear Koke</button>
            </form>
        </div>
    )
}

export default CreatePoke
