import React from "react"
import { useEffect , useState } from "react"
import { useDispatch , useSelector , connect } from "react-redux"
import { getAllPoke } from "../actions"
import Pokemon from "./Pokemon"

const Home = () => {


    let pokemons = useSelector((state) => state.pokemons);
    let dispatch = useDispatch();
    useEffect(()=>{
        //consolemo.log(pokemons)   
        dispatch(getAllPoke());
        console.log(pokemons)
    },[dispatch]);

    //const someState = useSelector(state=>state.myState.pokemons, shallowEqual)
    //const dispatch = useDispatch();

    return (
        <div>
            <button>Mostrar Pokemones</button>
            {
                pokemons ? pokemons.map(obj=>{
                   return <Pokemon name={obj.name} life={obj.life} strong={obj.strong} defense={obj.defense} speed={obj.speed} 
                   height={obj.height} weight={obj.weight} key={obj.ID} img={obj.img} types={obj.type}/>
                }) : <h5>"Cargando..."</h5>
            }
        </div>
    )
}
