import axios from "axios"

export function getAllPoke () {
    return function (dispatch){
        let poke = fetch("localhost:3001/pokemons").then(obj=> obj.json())
        return dispatch({
            type : "GET_ALL_POKEMONS",
            payload : poke
        })
    }
}
