import {getAllPoke} from "../actions"

const initialState = {
    pokemons : [],
    allPokemons : [],
    pokemon : {},
    types : [],
}

function rootReducer (state = initialState , action) {
    switch(action.type){
        case "GET_ALL_POKEMONS":
            return {
                ...state,
                pokemons : action.payload,
                allPokemons : action.payload
            }
        case "GET_ONE_POKEMON":
            return {
                ...state,
                pokemon : action.payload
            }
        case "CREATE_POKE":
            return {
                ...state,
                pokemons : state.pokemons.concat(action.payload)
            }
        case "GET_TYPES" :
            return {
                ...state,
                types : action.payload
            }
        case "FILTER_BY_TYPE":
            let pokes = state.allPokemons;
            let filterByType = action.payload === " " ? state.allPokemons : pokes.filter(obj => action.payload === obj.types[0].name);
            //let filterByType = pokes.filter(obj => obj.types.name.includes(action.payload))
            return {
                ...state,
                pokemons : filterByType
            }
        case "FILTER_BY_ORIGIN":
            let pokesByOrigin = [];
            
            if(action.payload === "All"){
                pokesByOrigin = state.allPokemons
            } else if (action.payload === "Db") {
                pokesByOrigin = state.allPokemons.filter (obj => obj.createdInDb)
            } else if (action.payload === "Api") {
                pokesByOrigin = state.allPokemons.filter (obj => !obj.createdInDb)
            }
            return {
                ...state,
                pokemons : pokesByOrigin
            }            
            default: return {...state};
    }
    
}

export default rootReducer
